import React, { Component } from 'react'
import { Fragment } from 'react/cjs/react.production.min';

export default class PlanetDropdown extends Component {

  onSelect = event => {
    this.props.selectItem(this.props.dropdownId, event.target.value);
  };

  render() {
    return (
      <Fragment>
      <div className="destination-box" id={'destination_'+this.props.id}>
        <div className="destination-box_top">
          <span className="destination-box_top_title">
            Destination { this.props.id }
          </span>
          <div className="destination-box_dropdown" >
            <select id="destination_dropdown_X" onChange={this.onSelect}>
              <option>Choose from the option</option>
              {
                this.props.planets.map((planet,index) => { 
                  return <option value={planet.name}
                              key={index}
                              distance={planet.distance}
                              index={index} 
                            >
                                {planet.name}
                            </option>
                })
              }
              </select>
          </div>
        </div>
          <div className="destination-box_bottom">
                <div className="vehicles" data-type="vehicles-checkbox" data-radio-checkbox id={'vehicles_'+this.props.id}>
                  <input type="radio" id={'vehicles_'+this.props.id+"1"} name="data-radio-vehicle" value="vehicles 1" />
                  <label htmlFor="vehicles-1">vehicles 1</label>
                  <input type="radio" id={'vehicles_'+this.props.id+"2"} name="data-radio-vehicle" value="vehicles 2" />
                  <label htmlFor="vehicles-2">vehicles 2</label>
                  <input type="radio" id={'vehicles_'+this.props.id+"3"} name="data-radio-vehicle" value="vehicles 3" />
                  <label htmlFor="vehicles-3">vehicles 3</label>
                  <input type="radio" id={'vehicles_'+this.props.id+"4"} name="data-radio-vehicle" value="vehicles 4" />
                  <label htmlFor="vehicles-4">vehicles 4</label>
                </div>
        </div>
      </div>   
      </Fragment>
    )
  }
}
