import React, { Component } from 'react'
import { Fragment } from 'react/cjs/react.production.min';

export default class PlanetDropdown extends Component {

  onSelect = event => {
    this.props.selectItem(this.props.dropdownId, event.target.value);
  };

  render() {
    return (
      <Fragment>
        <span className="destination-box_top_title">
          Destination { this.props.id }
        </span>
        <div className="destination-box_dropdown" >
          <select id="destination_dropdown_X" onChange={this.onSelect}>
            <option>Choose from the option</option>
            {
              this.props.planets.map((planet,index) => { 
                return <option value={planet.name}
                            key={index}
                            distance={planet.distance}
                            index={index} 
                          >
                              {planet.name}
                          </option>
              })
            }
          </select>
        </div>
      </Fragment>
    )
  }
}
