import $ from 'jquery';
import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.scss';
import PlanetDropdown from './components/PlanetDropdown'

function App() {
  const [planets, setPlanet] = useState([]);
  const [vehicles, setVehicle] = useState([]);
  const [selectedPlanet, setSelectedPlanet] = useState([]);
  const [selectedVehicle, setSelectedVehicle] = useState([]);
  const [selectedVehicleAvial, setselectedVehicleAvial] = useState([]);
  const [vehicleByDestination, setVehicleByDestination] = useState([]);

  const planetURL = 'https://5f5ff7f790cf8d00165573ed.mockapi.io/planets';
  const vehicleURL = 'https://5f5ff7f790cf8d00165573ed.mockapi.io/vehicles';

  useEffect(() => {
    const getPlanets = async () => {
      const dataPlanet = await fetchData(planetURL);
      console.log("dataPlanet")
      console.log(dataPlanet)
      setPlanet(dataPlanet);
    }

    const getVehicles = async () => {
      const dataVehicle = await fetchData(vehicleURL);
            console.log("dataVehicle")
      console.log(dataVehicle)
      setVehicle(dataVehicle);
      setSelectedVehicle(dataVehicle);
      setselectedVehicleAvial(dataVehicle);
    }

    const createVehicleByDestinationList = async () =>{
      const VbyDdata = await fetchData(vehicleURL);
      const VbyD = [
        {'destination-1': {VbyDdata}},
        {'destination-2': {VbyDdata}},
        {'destination-3': {VbyDdata}},
        {'destination-4': {VbyDdata}},
      ]
      setVehicleByDestination(VbyD);
    }


    getPlanets();
    getVehicles();
    createVehicleByDestinationList();
  }, [])



  // Fetch data from url
  const fetchData = async (url) => {
    const response = await fetch(url);
    const data = await response.json();
    return data
  }

  // filter option from dropdown
  const getFilteredPlanets = (name) => {
    const removedItems = { ...selectedPlanet};
    delete removedItems[name];
    const removedItemsList = Object.values(removedItems);
    return planets.filter(item => {
      return !removedItemsList.includes(item.name);
    });
  }

  const getPlanet = (name) => {
    return selectedPlanet[name];
  }

  const onSubmit = (e) => {
    e.preventDefault()


    // 

    console.log("onSubmit")
    console.log(selectedPlanet)
  }

  // on select show the next box
  const revealnextBox = (containerID,e) => {
    // console.log("selectedPlanet")
    // console.log(selectedPlanet)
    // console.log('reveal this :'+containerID)
    let dataEle = '[data-destination-container]';
    let thisContainer =  $(e).closest(dataEle);
    let nextContainer = thisContainer.next(dataEle);

    // adding reveal class to the box next to selected box
    nextContainer.addClass('reveal');

  }

  const selectItem = (containerID, value) => {
    console.log("selectItem : " + containerID + " and " + value)
    setSelectedPlanet({
      ...selectedPlanet,
      [containerID]: value
    });

  };

  const findAvialShip = () =>{
    console.log('findAvialShip');
  }

  const selectVehicle = (containerID, value, e) => {
    setSelectedVehicle({
      ...selectedVehicle,
      [containerID]: value
    })

    // // to the closest fieldset parent
    // let fieldsetWrapper = $(e).closest('fieldset');
    // let fieldsetWrapperID = fieldsetWrapper[0].id;
    // // decease total_no -1 per select selectedVehicleAvial
    // // console.log("fieldsetWrapper")
    // // console.log(fieldsetWrapper)
    // // console.log("fieldsetWrapperID")
    // // console.log(fieldsetWrapperID)
    // // console.log('value selected :'+value)
    // for(var i = 0; i<selectedVehicleAvial.length; i++){
    //   if(selectedVehicleAvial[i].name.toLowerCase() === value.toLowerCase()){
    //     let availNo = selectedVehicleAvial[i].total_no;
    //     if(availNo !== 0){
    //       console.log('still has it')
    //       console.log(selectedVehicle)
    //     }
    //     console.log(selectedVehicleAvial[i]);
    //     console.log("availNo :"+availNo);
    //     // selectedVehicleAvial();
    //   }
    // }
    // // const removedItems = { ...selectedPlanet};
    // // delete removedItems[name];
    // // const removedItemsList = Object.values(removedItems);
    // // return planets.filter(item => {
    // //   return !removedItemsList.includes(item.name);
    // // });
    // findAvialShip();
  };


  return (
    <div className="App">
      <header className="App-header">
        <h1>Finding Falcone</h1>
        <p>Select planets you want to search in:</p>
      </header>
      <div className="missioncontroll">
        <div className="missioncontroll_container">

                {
                (planets.length > 0 && vehicles.length > 0 && vehicleByDestination.length >0) ? (
                  <React.Fragment>
                  <PlanetDropdown
                    name='destination-1'
                    vehicles={vehicles}
                    selectedVehicle={selectedVehicle}
                    selectVehicle={ selectVehicle}
                    VbyD={vehicleByDestination}
                    planets={getFilteredPlanets('destination-1')}
                    value={getPlanet('destination-1')}
                    dropdownId='destination-1'
                    revealnextBox={revealnextBox}
                    selectItem={selectItem} id='1'
                    reveal={true} // show first box on load
                    />
                  <PlanetDropdown
                    name='destination-2'
                    vehicles={vehicles}
                    selectedVehicle={selectedVehicle}
                    selectVehicle={ selectVehicle}
                    VbyD={vehicleByDestination}
                    planets={getFilteredPlanets('destination-2')}
                    value={getPlanet('destination-2')}
                    dropdownId='destination-2'
                    revealnextBox={revealnextBox}
                    selectItem={selectItem} id='2'/>
                  <PlanetDropdown
                    name='destination-3'
                    vehicles={vehicles}
                    selectedVehicle={selectedVehicle}
                    selectVehicle={ selectVehicle}
                    VbyD={vehicleByDestination}
                    planets={getFilteredPlanets('destination-3')}
                    value={getPlanet('destination-3')}
                    dropdownId='destination-3'
                    revealnextBox={revealnextBox}
                    selectItem={selectItem} id='3'/>
                  <PlanetDropdown
                    name='destination-4'
                    vehicles={vehicles}
                    selectedVehicle={selectedVehicle}
                    selectVehicle={ selectVehicle}
                    VbyD={vehicleByDestination}
                    planets={getFilteredPlanets('destination-4')}
                    value={getPlanet('destination-4')}
                    dropdownId='destination-4'
                    revealnextBox={revealnextBox}
                    selectItem={selectItem} id='4'/>
                  </React.Fragment>

                ) : (
                    'no data to show'
                  )
                }


        </div>

        <div className="missioncontroll_form">
          <form className='fiding-form' onSubmit={onSubmit}>
            <div className='form-control'>
              <label htmlFor="destination">placeholder</label>
              <input type="text" data-finding-form-value/>
            </div>
            <input type="submit" value="Find Falcone!" className='btn'/>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
