import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import PlanetDropdown from './components/PlanetDropdown'

function App() {
  const [planets, setPlanet] = useState([]);
  const [vehicles, setVehicle] = useState([]);
  const [selectedPlanet, setSelectedPlanet] = useState([]);

  const planetURL = 'https://5f5ff7f790cf8d00165573ed.mockapi.io/planets';
  const vehicleURL = 'https://5f5ff7f790cf8d00165573ed.mockapi.io/vehicles';

  useEffect(() => {
    const getPlanets = async () => {
      const dataPlanet = await fetchData(planetURL);
      console.log("dataPlanet")
      console.log(dataPlanet)
      setPlanet(dataPlanet);
    }

    const getVehicles = async () => {
      const dataVehicle = await fetchData(vehicleURL);
            console.log("dataVehicle")
      console.log(dataVehicle)
      setVehicle(dataVehicle);
    }


    getPlanets();
    getVehicles();
  }, [])


  const onSubmit = (e) => {
    e.preventDefault()

    console.log("onSubmit")
  }

  // Fetch data from url
  const fetchData = async (url) => {
    const response = await fetch(url);
    const data = await response.json();
    return data
  }

  // filter option from dropdown
  const getFilteredPlanets = (name) => {
    console.log("getFilteredPlanets name:" + name)
    const removedItems = { ...selectedPlanet};
        console.log("removedItems");
    console.log(removedItems);
    delete removedItems[name];
    const removedItemsList = Object.values(removedItems);
    console.log("removedItemsList");
    console.log(removedItemsList);

    return planets.filter(item => {
      return !removedItemsList.includes(item.id);
    });
  }

  const selectItem = (name, value) => {
    console.log("selectItem : " + name + " and " + value)
    setSelectedPlanet({
      ...selectedPlanet,
      [name]: value
    });
    console.log('selectedPlanet')
    console.log(selectedPlanet)
  };


  return (
    <div className="App">
      <header className="App-header">
        <h1>Finding Falcone</h1>
        <p>Select planets you want to search in:</p>
      </header>
      <div className="missioncontroll">
        <div className="missioncontroll_container">

            <div className="destination-box" id="destination_X">

              <div className="destination-box_top">
                {
                (planets.length > 0 && vehicles.length > 0) ? (
                  <React.Fragment>
                  <PlanetDropdown
                    planets={getFilteredPlanets('destination-1')}
                    dropdownId='destination-1'
                    selectItem={selectItem} id='1'/>
                  <PlanetDropdown
                    planets={getFilteredPlanets('destination-2')}
                    dropdownId='destination-2'
                    selectItem={selectItem} id='2'/>
                  <PlanetDropdown
                    planets={getFilteredPlanets('destination-3')}
                    dropdownId='destination-3'
                    selectItem={selectItem} id='3'/>
                  <PlanetDropdown
                    planets={getFilteredPlanets('destination-4')}
                    dropdownId='destination-4'
                    selectItem={selectItem} id='4'/>
                  </React.Fragment>

                ) : (
                    'no data to show'
                  )
                }


              </div>

              <div className="destination-box_bottom">
                <div className="vehicles" data-type="vehicles-checkbox" data-radio-checkbox id="vehicles_X">
                  <input type="radio" id="vehicles-1" name="data-radio-vehicle" value="vehicles 1" />
                  <label htmlFor="vehicles-1">vehicles 1</label>
                  <input type="radio" id="vehicles-2" name="data-radio-vehicle" value="vehicles 2" />
                  <label htmlFor="vehicles-2">vehicles 2</label>
                  <input type="radio" id="vehicles-3" name="data-radio-vehicle" value="vehicles 3" />
                  <label htmlFor="vehicles-3">vehicles 3</label>
                  <input type="radio" id="vehicles-4" name="data-radio-vehicle" value="vehicles 4" />
                  <label htmlFor="vehicles-4">vehicles 4</label>
                </div>
              </div>

            </div>

        </div>

        <div className="missioncontroll_form">
          <form className='fiding-form' onSubmit={onSubmit}>
            <div className='form-control'>
              <label htmlFor="destination">placeholder</label>
              <input type="text" data-finding-form-value/>
            </div>
            <input type="submit" value="Find Falcone!" className='btn'/>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
