import $ from 'jquery';
import React, { Component } from 'react'
import { Fragment } from 'react/cjs/react.production.min';
import VehicleRadio from '../components/VehicleRadio'

export default class PlanetDropdown extends React.Component {
  constructor(props) {
    super(props);
    this.reveal = (this.props.reveal) ? ('reveal') : ('');
    this.containerClass = 'destination-box'+' '+this.reveal; // spacing for classname
    this.selectClass = 'destination_dropdown_'+this.props.id;
    console.log("this.props.vehicles")
    console.log(this.props.vehicles)
  }

  onSelectPlanet = event => {
    this.props.selectItem(this.props.name, event.target.value);
    this.props.revealnextBox(this.props.dropdownId, event.target);
  };

  onSelectVehicles = event => {
    console.log("onSelectVehicles")
    this.props.selectVehicle(this.props.name,event.target.value, event.target);



    console.log(event.target)
    // to the closest fieldset parent
    let fieldsetWrapper = $(event.target).closest('fieldset');
    let fieldsetWrapperID = fieldsetWrapper[0].id;

    fieldsetWrapper.attr('selected',true)
    console.log(fieldsetWrapper.children())
    // fieldsetWrapper.child('input').attr('test',true);
    fieldsetWrapper.forEach( test =>{
      console.log(test)
    })
    // decease total_no -1 per select selectedVehicleAvial
    console.log("fieldsetWrapper")
    console.log(fieldsetWrapper)
    console.log("fieldsetWrapperID")
    console.log(fieldsetWrapperID)

    // console.log('value selected :'+value)
    // for(var i = 0; i<selectedVehicleAvial.length; i++){
    //   if(selectedVehicleAvial[i].name.toLowerCase() === value.toLowerCase()){
    //     let availNo = selectedVehicleAvial[i].total_no;
    //     if(availNo !== 0){
    //       console.log('still has it')
    //       console.log(selectedVehicle)
    //     }
    //     console.log(selectedVehicleAvial[i]);
    //     console.log("availNo :"+availNo);
    //     // selectedVehicleAvial();
    //   }
    // }
    // const removedItems = { ...selectedPlanet};
    // delete removedItems[name];
    // const removedItemsList = Object.values(removedItems);
    // return planets.filter(item => {
    //   return !removedItemsList.includes(item.name);
    // });

  }

  // filter radio option
  getFilteredVehicles = (name) => {
    console.log("name")
    console.log(name)
    // const selectedVehicle = { ...this.props.selectedVehicle};
    // console.log("selectedVehicle");
    // console.log(selectedVehicle);
    // // delete selectedVehicle[name];
    // const updatedAvailList = Object.values(selectedVehicle);
    // console.log("updatedAvailList")
    // console.log(updatedAvailList)
    // return this.props.vehicles.filter(item => {

    //   return !updatedAvailList.includes(item.name);
    // });
    return this.props.vehicles
  }

  render() {
    return (
      <Fragment>
      <div className={this.containerClass} id={'destination_'+this.props.id}
        data-destination-container
      >
        <div className="destination-box_top">
          <span className="destination-box_top_title">
            Destination { this.props.id }
          </span>
          <div className="destination-box_dropdown" >
            <select id={this.selectClass} onChange={this.onSelectPlanet} value={this.props.value}>
              <option>Choose from the option</option>
              {
                this.props.planets.map((planet,index) => { 
                  return <option value={planet.name}
                              key={index}
                              distance={planet.distance}
                              index={index} 
                            >
                                {planet.name}
                            </option>
                })
              }
              </select>
          </div>
        </div>
        {
          (this.props.vehicles.length > 0 ) ? (
            <div className="destination-box_bottom">
              <div className="vehicles" data-type="vehicles-checkbox" data-radio-checkbox id={'vehicles_'+this.props.id}>
                <fieldset id={'vehicles_group_'+this.props.id} onChange={this.onSelectVehicles}>
                {
                  this.props.vehicles.map((vehicle,index)=>{
                    return <VehicleRadio vehicle={vehicle} 
                      groupid={'vehicles_group_'+this.props.id}
                      name={this.props.name}
                      index={index} id={index+1} key={index}></VehicleRadio>
                  })
                }
                </fieldset>
              </div>
            </div>
          ) : (
            'No vehicle data'
          )
        }
      </div>   
      </Fragment>
    )
  }
}
