import React from 'react'

export default function planetDropdown(planets) {
  console.log("this from dropdown planets")
  console.log(planets)
  const onSelect = event => {
    this.props.selectItem(this.props.dropdownId, event.target.value);
  };

  return (
      <div className="destination-box_dropdown" >
        <select id="destination_dropdown_X" onChange={onSelect}>
          <option>Choose from the option</option>
          {
            planets.map((planet,index) => { 
               return <option value={planet.name}
                          key={index}
                          distance={planet.distance}
                          index={index} 
                        >
                            {planet.name}
                        </option>
            })
          }
        </select>
      </div>
  )
}


